create database customer_behavior;
-- 1. What is the total revenue generated by male vs. female customers?
select *from customer;
select gender,sum(purchase_amount)
from customer 
group by gender;

-- Q2. Which customers used a discount but still spent more than the average purchase amount? 
select * from customer;
select customer_id,purchase_amount
from customer 
where
discount_applied='Yes' and purchase_amount > (select avg(purchase_amount) from customer);

--  Q3. Which are the top 5 products with the highest average review rating?
select * from customer;
select item_purchased,round(avg(review_rating),2) from customer
 group by item_purchased 
 order by avg(review_rating) desc 
 limit 5;
 
 -- Q4. Compare the average Purchase Amounts between Standard and Express Shipping.
select * from customer;
select shipping_type,avg(purchase_amount)
from customer
where shipping_type='Express' or
shipping_type="standard"
group by shipping_type;

-- Q5 Do subscribed customers spend more? Compare average spend and total revenue between subscribers and non-subscribers.
select * from customer;
SELECT subscription_status,
       COUNT(customer_id) AS total_customers,
       ROUND(AVG(purchase_amount),2) AS avg_spend,
       ROUND(SUM(purchase_amount),2) AS total_revenue
FROM customer
GROUP BY subscription_status
ORDER BY total_revenue,avg_spend DESC;

-- Q6 Which 5 products have the highest percentage of purchases with discounts applied?
select * from customer;
select item_purchased as products,
count(item_purchased) as highest_percenatge 
from customer
where 
discount_applied='Yes'
group by item_purchased
order by highest_percenatge desc limit 5;

SELECT item_purchased,    /*based on the discount rate by applying discount*/
       ROUND(100.0 * SUM(CASE WHEN discount_applied = 'Yes' THEN 1 ELSE 0 END)/COUNT(*),2) AS discount_rate
FROM customer
GROUP BY item_purchased
ORDER BY discount_rate DESC
LIMIT 5;

-- 7. Segment customers into New, Returning, and Loyal based on their total number of previous purchases, and show the count of each segment. 
select distinct previous_purchases from customer order by previous_purchases desc;
with customer_type as (
select customer_id ,previous_purchases,
case
when previous_purchases = 1 then 'New'
when previous_purchases between 2 and 10 then 'returning'
else 'loyal'
end as customer_segment
from customer)
select customer_segment,count(*) from customer_type
group by customer_segment;

-- Q8. What are the top 3 most purchased products within each category? 
WITH item_counts AS (
    SELECT category,
           item_purchased,
           COUNT(customer_id) AS total_orders,
           ROW_NUMBER() OVER (PARTITION BY category ORDER BY COUNT(customer_id) DESC) AS item_rank
    FROM customer
    GROUP BY category, item_purchased
)
SELECT item_rank,category, item_purchased, total_orders
FROM item_counts
WHERE item_rank <=3;


-- Q9. Are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?
select * from customer;
select subscription_status,count(customer_id)
from customer
where 
previous_purchases >5
group by subscription_status;

-- Q10. What is the revenue contribution of each age group? 
select * from customer;
select age_group,
sum(purchase_amount)as total_revenue_contribution
from customer
group by age_group
order by total_revenue_contribution;
